import React, { useState } from 'react';
import ImageDisplay from './ImageDisplay'; // ImageDisplayコンポーネントのインポート

const FileUpload: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [processedImageName, setProcessedImageName] = useState<string | null>(null); // 加工された画像のファイル名を保持するState

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (files && files.length > 0) {
      setSelectedFile(files[0]);
    }
  };

  const handleUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);

      try {
        const response = await fetch('http://localhost:3001/process-image', {
          method: 'POST',
          body: formData
        });

        if (response.ok) {
          const processedImageName = await response.text(); // 加工された画像のファイル名を受け取る
          setProcessedImageName(processedImageName); // レスポンスデータそのものが加工された画像のURLになる
        } else {
          console.error('Error uploading image');
        }
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} />
      {selectedFile && <p>Selected File: {selectedFile.name}</p>}
      <button onClick={handleUpload}>Upload</button>
      {/* 加工された画像のファイル名を使用して画像を表示 */}
      {processedImageName && <img src={`http://localhost:3001/processed_images/${processedImageName}`} alt="Processed Image" />}
    </div>
  );
};

export default FileUpload;
