import React, { useState } from 'react';

const FileUpload: React.FC = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [processedImage, setProcessedImage] = useState<string | null>(null);
  const [score, setScore] = useState<number | null>(null); // スコアを保持するState

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (files && files.length > 0) {
      setSelectedFile(files[0]);
    }
  };

  const handleUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);

      try {
        const response = await fetch('http://localhost:3001/process-image', {
          method: 'POST',
          body: formData
        });

        if (response.ok) {
          // 画像データのURLをセット
          const processedImageData = await response.blob();
          const processedImageUrl = URL.createObjectURL(processedImageData);
          setProcessedImage(processedImageUrl);

          // スコアをセット
          const result = await response.json(); // JSON形式のレスポンスを解析
          if (result && typeof result.score === 'number') {
            setScore(result.score);
          } else {
            console.error('Invalid score received');
          }
        } else {
          console.error('Error uploading image');
        }
      } catch (error) {
        console.error(error);
      }
    }
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} />
      {selectedFile && <p>Selected File: {selectedFile.name}</p>}
      <button onClick={handleUpload}>Upload</button>
      {processedImage && <img src={processedImage} alt="Processed Image" />}
      {score && <p>Score: {score}</p>} {/* スコアを表示 */}
    </div>
  );
};

export default FileUpload;
