import React, { useState, useEffect } from 'react';

const ImageDisplay = () => {
  // 画像のURLを保持するstate
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    // サーバーから画像を取得する関数
    const fetchImage = async () => {
      try {
        // サーバーから画像を取得するAPIエンドポイントを呼び出す
        const response = await fetch('/process-image');
        // レスポンスからBlobオブジェクトを取得する
        const blob = await response.blob();
        // Blobオブジェクトから画像のURLを生成する
        const url = URL.createObjectURL(blob);
        // 画像のURLをstateに設定する
        setImageUrl(url);
      } catch (error) {
        console.error('Error fetching image:', error);
      }
    };

    // fetchImage関数を呼び出して画像を取得する
    fetchImage();

    // コンポーネントのアンマウント時に画像のURLを解放する
    return () => {
      URL.revokeObjectURL(imageUrl);
    };
  }, []); // 第二引数の空配列により、このEffectはマウント時のみ実行される

  return (
    <div>
      {/* 画像があれば表示 */}
      {imageUrl && <img src={imageUrl} alt="Processed Image" />}
    </div>
  );
};

export default ImageDisplay;
